import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      monsters: [
        {
          id: 'a1',
          name: 'Dracula'
        },
        {
          id: 'a2',
          name: 'Frankenstein'
        },
        {
          id: 'a3',
          name: 'Batman'
        }
      ]
    }
  }

  render() {
      return (
      <div className="App">
        {
          this.state.monsters.map(monster => 
            <h1>{monster.name}</h1>
          )

        }
      </div>
    );
  }
}

export default App;
